mapper.select:
select * from board
	where bo_bt_num = (select bt_num from `board-type` where bt_types like '팀게시판' and bt_tm_num = #{tm.tm_num})
		and bo_title like concat('%', #{cri.search},'%')
		
			and bo_bc_num = #{cri.type}
		
		order by bo_num desc
		limit #{cri.pageStart}, #{cri.perPageNum}

mapper.insert:
INSERT INTO `board-reply`(br_ori_num, br_groupOrd, br_groupLayer, br_toward_num, br_bo_num, br_me_id, br_contents, br_register_date, br_state) select
#{br.br_ori_num}, IFNULL((select gO from (SELECT br_groupOrd as gO FROM `board-reply` WHERE br_ori_num=#{br.br_ori_num} AND br_groupOrd > #{br.br_groupOrd} AND #{br.br_groupLayer} >= br_groupLayer ORDER BY br_groupOrd LIMIT 1) as tmpT), IFNULL(max(br_groupOrd),0)+1), #{br.br_groupLayer},
#{br.br_toward_num}, #{br.br_bo_num}, #{br.br_me_id}, #{br.br_contents}, NOW(), 'n' from `board-reply` where br_ori_num = #{br.br_ori_num}

mapper.select:
select * from `board-reply`
	where br_bo_num = #{bo_num}
	order by br_ori_num, br_groupOrd 
	limit #{cri.pageStart}, #{cri.perPageNum}

mapper.delete:
	delete from `board-file`
		where bf_num = #{bf_num}
		and bf_bo_num is null

mapper.update:
	update `board`
		set bo_title = #{bo.bo_title},
			bo_content = #{bo.bo_content},
			bo_bc_num = #{bo.bo_bc_num}
		where bo_num = #{bo.bo_num}

update `board-reply`
     set br_groupOrd = br_groupOrd + 1
    	 where br_ori_num = #{br.br_ori_num}
    		 and br_groupOrd >= #{br.br_groupOrd}
    		 and br_num != #{br.br_num}
   	 	 and br_groupLayer != #{br.br_groupLayer};

choose.when:
		update `board-reply`
    	 	set br_groupOrd = br_groupOrd + 1
   	  	where br_num = #{br.br_num};

choose.otherwise:
update `board-reply`
	set br_groupOrd = br_groupOrd + 1
	where br_num !=#{br.br_num} and br_groupOrd=#{br.br_groupOrd}

mapper.select:
select count(*) from `board-reply`
	where br_ori_num = #{br.br_ori_num}
		and br_groupOrd = #{br.br_groupOrd}
		and br_groupLayer = #{br.br_groupLayer}
