mapper.update:
update `team-player`
	set tp_auth = #{tp.tp_auth},
		tp_backnum = #{tp.tp_backnum}
	where tp_num = #{tp.tp_num}

mapper.select:
select * from `playerrecord_year-hitter`
	join `team-player` on `playerrecord_year-hitter`.yh_tp_num and `team-player`.tp_num
	where tp_pl_num = #{pl_num}

select * from `playerrecord_year-pitcher`
	join `team-player` on `playerrecord_year-pitcher`.yp_tp_num and `team-player`.tp_num
	where tp_pl_num = #{pl_num}
