mapper.select:
		select * from team
		where
  			(tm_name like concat('%',#{cri.search} ,'%'))
  			
  			and tm_teamstate like #{state}
  			
  			
  				and tm_re_num= #{cri.type}  				
  			
  			
  			order by tm_num desc
			limit #{cri.pageStart}, #{cri.perPageNum}

		select * from team
		where
  			(tm_name like concat('%',#{cri.search} ,'%'))
  			
  				and tm_re_num= #{cri.type}  				
  			
  			order by tm_num desc
			limit #{cri.pageStart}, #{cri.perPageNum}

		select
			count(tm_num)
		from
			team
		where tm_teamstate like #{state}
			
  				and tm_re_num= #{cri.type}

mapper.update:
update team
	set tm_teamstate = #{state}
	where tm_num = #{tm_num}
